namespace JochCool.AdventOfCode.Year2020.Day02;  public static class Part2 { 	static readonly Regex onePassword = new Regex(@"^(\d+)-(\d+) +(\w): *(\w*)\r?$", RegexOptions.Multiline);  	public static string? Solve(TextReader inputReader) 	{ 		int result = 0;  		// Find all (properly formatted) lines in the file 		Match match = onePassword.Match(inputReader.ReadToEnd()); 		while (match.Success) 		{ 			// get params 			int first = int.Parse(match.Groups[1].Value) - 1; 			int second = int.Parse(match.Groups[2].Value) - 1; 			char expected = match.Groups[3].Value[0]; 			string toTest = match.Groups[4].Value; 			Console.WriteLine($"First: {first}, Second: {second}, char: {expected}, to test: {toTest}");  			if (toTest[first] == expected ^ toTest[second] == expected) 			{ 				Console.WriteLine("Valid."); 				result++; 			}  			match = match.NextMatch(); 		}  		Console.WriteLine($"Number of valids: {result}.");  		return result.ToInvariantString(); 	} } 